import { Component } from '@angular/core';
import { GameService } from 'src/app/game.service';

@Component({
  selector: 'app-tile',
  templateUrl: './tile.component.html',
  styleUrls: ['./tile.component.css']
})
export class TileComponent {
  buildingsAvailable : string[] = []
  buildingPlaced = this.buildingsAvailable[this.randomizedBuildings()];

  constructor(gameservice: GameService) {
    this.buildingsAvailable = gameservice.buildings
  } 

  randomizedBuildings() : number {
    console.log(Math.floor(Math.random()*this.buildingsAvailable.length));
    console.log(this.buildingsAvailable.length());

    return Math.floor(Math.random()*this.buildingsAvailable.length)
  }
}
<div *ngFor="let building of buildingsAvailable" class="border flex justify-center items-center align-middle bg-blue-600 shadow-md w-1/4 h-36 cursor-pointer">
    <p class="text-7xl align-middle">{{buildingPlaced}}</p>
</div>
